import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

//   CREO COMPONENTE REACT SQUARE

class Square extends React.Component {

    // Dedinisco un costruttore per poi salvarmi quando ho cliccato lo square
    // To remember the CONSTRUCTOR of components use STATE.
    // always call SUPER when defining the constructor of a component
    constructor(props) {
        super(props);
        this.state = {
            value: null,
        };
    }


    render() {
      return (
                    // <button className="square" onClick={function() {
                    //     console.log('click'); }}>
                    //   {this.props.value}
                    // </button>

        <button className="square" onClick={() => 
            //  cambio il valore dello stato del quadrato in X quando lo clicco
            this.setState({value: 'X'})}
            //  con arrow function syntax non bisogna ggiungere il punto e virgola 
        >  
            {this.state.value}
        </button>
      );
    }
  }




//   parent BOARD

  class Board extends React.Component {

    //   CREIAMO un constructor per gestire gli stati degli square figli
    constructor(props) {
        super(props);
        this.state = {
            squares: Array(9).fill(null),
        };
    }

    renderSquare(i) {
      return <Square value={this.state.squares[i]} />;
    }

    render() {
      const status = 'Next player: X';

      return (
        <div>
          <div className="status">{status}</div>
          <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
          </div>
          <div className="board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
          </div>
          <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
          </div>
        </div>
      );
    }
  }

  class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }

  // ========================================

  ReactDOM.render(
    <Game />,
    document.getElementById('root')
  );
